INSERT INTO `posts` (`id`, `title`, `summary`, `content`, `images`, `created_at`, `updated_at`, `user_id`, `cate_id`) VALUES
(11, 'Đã bao lâu rồi bạn không yêu', '<p><span style=\"color:rgb(33, 25, 34); font-family:tahoma,geneva,sans-serif\">Tôi chắc rằng có rất nhiều người giống tôi không biết thời gian đó là bao lâu phải không? Chỉ biết rằng cảm xúc ngày nào chẳng còn hiền hiện</span></p>', '<div class=\"viewcontent-page\" style=\"margin: 0px; padding: 60px 0px 10px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; line-height: inherit; font-family: Tahoma, Geneva, sans-serif; vertical-align: baseline; max-width: 100%; width: 610px; float: left; color: rgb(33, 25, 34);\">\r\n<h1>ĐÃ BAO LÂU RỒI BẠN KHÔNG YÊU?</h1>\r\n\r\n<div class=\"maincontent\" style=\"margin: 10px 0px; padding: 10px 0px 60px; border-width: 0px 0px 2px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(227, 29, 126); border-left-color: initial; border-image: initial; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%; width: 610px; float: left;\">\r\n<p style=\"text-align:justify\"><strong>– Đã bao lâu rồi bạn không yêu?</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Đã có ai hỏi bạn câu đó chưa?</strong></p>\r\n\r\n<p style=\"text-align:center\">**</p>\r\n\r\n<p style=\"text-align:justify\">Tôi chắc rằng có rất nhiều người giống tôi không biết thời gian đó là bao lâu phải không? Chỉ biết rằng cảm xúc ngày nào chẳng còn hiền hiện. Chỉ biết rằng lòng luôn xao động với những mớ kí ức ngổn ngang, dòng tâm sự mỗi ngày vẫn chứa chan nỗi niềm xưa cũ. Một tình yêu bỗng quá xa xôi, bởi lòng ta đã hóa thành sắt đá mãi sống trong hoài niệm.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"da-bao-lau-roi-ban-khong-yeu\" src=\"http://www.truyenngan.com.vn/images/Phuongvtm/2017-02/da-bao-lau-roi-ban-khong-yeu.jpg\" style=\"border:0px; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:inherit; margin:0px; max-width:90%; padding:0px; vertical-align:baseline; width:595px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Có ai đó từng nói với tôi rằng: Đừng nghĩ mãi về quá khứ, nó chỉ mang tới những giọt nước mắt. Đừng nghĩ nhiều về tương lai, nó chỉ mang lại nỗi lo sợ. Hãy sống ở hiện tại với nụ cười trên môi, nó sẽ đem lại niềm vui cho bạn và những người xung quanh.</p>\r\n\r\n<p style=\"text-align:justify\">Cũng có ai đó từng mong thời gian trôi đi thật nhanh chỉ để quên đi một người. Nhưng cũng có ai đó từng mong thời gian ngừng trôi để họ lưu giữ trọn một khoảnh khắc nào đó trong cuộc đời. Và tôi cũng vậy. Tôi cũng muốn lưu lại khoảnh khắc của cuộc đời mỗi khi tết đến xuân về.</p>\r\n\r\n<p style=\"text-align:justify\">Xuân đem đến cho ta cảm xúc lâng lâng, khó diễn tả thành lời. Cứ nghĩ trái tim sắt đá sẽ mãi băng giá như tuyết lạnh mùa đông. Nhưng xuân về cũng khiến trái tim có phút giây rung động xao xuyến nơi lồng ngực. Nó lại bắt đầu thổn thức bởi những thứ tình cảm thiêng liêng. Tình cảm gia đình, tình cảm anh chị em, tình cảm bạn bè và tình cảm từ những mối quan hệ không được đặt tên. Những tình cảm đó được xích lại gần hơn bao giờ khi tết đến xuân về. Xuân cho ta những giây phút thiêng liêng khi bên gia đình trong đêm giao thừa ấm áp yêu thương. Xuân cho ta những nụ cười chứa chan khi gặp lại những người bạn cũ. Xuân cho ta những cái nắm tay, cái ôm thắm thiết trong bữa tiệc gặp mặt mừng xuân. Xuân cho ta những thẹn thùng, má đỏ, môi hồng khi bắt gặp ánh mắt lạ... chạm nhau, rồi mỉm cười hạnh phúc. Mùa xuân mùa của hi vọng, của những ước mơ...để ta lại muốn yêu và được yêu.</p>\r\n\r\n<p style=\"text-align:justify\">Vậy là chỉ còn vài ngày nữa là kết thúc một năm cũ và một năm mới lại bắt đầu. Ngoảnh đi ngoảnh lại mới thấy thời gian không bao giờ chờ đợi ai cả. Hãy sống sao để không lãng phí tuổi thanh xuân của mình. Hãy cứ hi vọng và khát khao về một tương lai tươi đẹp. Hạnh phúc là một hành trình chứ không phải là điểm đến. Vậy nên chúng ta đang đi tìm hạnh phúc trên hành trình đó và đó là cả một hành trình dài của cuộc đời.</p>\r\n\r\n<p style=\"text-align:justify\">Con đường đời phía trước còn rất dài nhưng đó không phải là lí do để ta đi chậm lại. Đó là lí do để ta bắt đầu, để trưởng thành, để tìm những con đường mới. Con đường phía trước còn dài và tràn đầy cơ hội ở từng ngã rẽ. Hãy bắt đầu điều cần bắt đầu, hãy kết thúc điều cần kết thúc.</p>\r\n\r\n<p style=\"text-align:justify\">Cám ơn mùa xuân đã lại đem đến cho ta niềm tin vào một mối quan hệ sắp tới. Cám ơn mùa xuân đã đem đến cho ta những cảm xúc mà bấy lâu nay ta bị lãng quên. Cám ơn mùa xuân đã cho ta thêm trưởng thành.</p>\r\n</div>\r\n</div>', '10.jpg', '2017-03-28 09:36:32', '2017-03-29 01:13:06', 3, 6),
(12, 'Em sẽ đến cùng cơn mưa - Ichikama Takuji', '<p><strong>Phải mất đến mấy ngày sau khi đọc xong \"Em sẽ đến cùng cơn mưa\", tôi mới có thể sắp xếp lại những cảm xúc xáo trộn đặc biệt mà trước nay, khi đọc các tác phẩm của các tác giả Nhật Bản, tôi chưa có được</strong></p>', '<p><span style=\"font-family:inherit; font-size:inherit\">Đây là một câu chuyện mà ngay cả nỗi đau cũng mang một vẻ nhẹ nhàng hiếm có, một câu chuyện mà khiến tôi quên đi mất cái điều tôi đang tìm kiếm trong những trang sách, một câu chuyện mà khi gấp lại, nếu không phải đang ở quán cafe và cố kìm nước mắt, có lẽ tôi đã khóc...</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:inherit; font-size:inherit\"><img alt=\"em sẽ đến cùng cơn mưa\" src=\"http://www.truyenngan.com.vn/images/Phuongvtm/2013.04/em-se-dem-cung-con-mua.jpg\" style=\"border:0px; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:inherit; margin:0px; max-width:90%; padding:0px; vertical-align:baseline; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-family:inherit; font-size:inherit\">Tôi muốn khóc, vì Takkun, người chồng thật tốt nhưng trớ trêu thay, lại sống với những cơn đau về thể xác, không làm được những việc mà một người bình thường có thể làm như đi thang máy, đi xe điện...Nhưng bằng tất cả những gì còn có được, anh vẫn yêu vợ mình với một tình yêu không ngơi nghỉ, dù cho anh phải đau nỗi đau tận cùng đến hai lần.</span></p>\r\n\r\n<p><span style=\"font-family:inherit; font-size:inherit\">Tôi muốn khóc, vì Yuji, cậu con trai đáng yêu, kết tinh của một mối tình đặc biệt, của hai người đặc biệt. Cậu bé sớm mất mẹ nhưng tỏ ra rất trưởng thành và kiên cường, nhưng làm sao giấu được cái khao khát được nũng nịu, được mẹ cài cho cái cúc áo, chuẩn bị cho hộp cơm trưa...như bao đứa trẻ khác?</span></p>\r\n\r\n<div id=\"AbdVideoInPagePlayerWrapper\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%; height: 0px; overflow: hidden;\">\r\n<div id=\"AbdVideoInPagePlayer\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%;\"><iframe frameborder=\"0\" id=\"ifvideoinpage\" scrolling=\"no\" src=\"http://media.adnetwork.vn/assets/player/jwp722/videoinpage-player-jwp722.html?flash=true&amp;volume=30&amp;autoStart=false&amp;skipAd=7&amp;tag=http%3A%2F%2Fdelivery.adnetwork.vn%2F247%2Fxmlvideoad%2Fwid_1377161727%2Fzid_1396944246%2Ftype_inline%2Feff_vidinpage%2Fcb_48661%2Fw_610%2Fh_344%2Fpurl_aHR0cDovL3d3dy50cnV5ZW5uZ2FuLmNvbS52bi9zYWNoLWhheS90YWMtcGhhbS1kdS1sdWFuLzQwODMtZW0tc2UtZGVuLWN1bmctY29uLW11YS5odG1s&amp;media_path=http%3A%2F%2Fmedia.adnetwork.vn%2Fassets%2F&amp;w=610&amp;h=344\" style=\"border-width: 0px; border-style: initial; width: 610px; height: 344px;\"></iframe></div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:inherit; font-size:inherit\">Tôi muốn khóc, vì Mio-\"hồn ma của tình yêu\". Tôi gọi nàng như vậy, vì nàng là \"hồn ma\", và nàng là \"tình yêu\" của một gia đình mong mỏi sống hạnh phúc cùng nhau. Nàng đã khiến tôi quên đi câu hỏi: \"Tại sao Mio lại xuất hiện bất thường như thế?\", \"Đây có thực sự là người vợ của Takkun hay không?\" và bị cuốn theo mạch truyện tự nhiên của gia đình nhỏ bé này.</span></p>\r\n\r\n<p><span style=\"font-family:inherit; font-size:inherit\">Nghe tôi nói thế, có thể mọi người sẽ nghĩ truyện này chắc là đầy nước mắt, đọc mệt lắm đây...</span></p>\r\n\r\n<p><span style=\"font-family:inherit; font-size:inherit\">Hoàn toàn không, Takkun không khóc, Mio không khóc, và Yuji lại càng không khóc. Họ chỉ có 6 tuần bên nhau, thời gian để yêu còn không đủ, lấy đâu thời gian để khóc cho cái sự chia ly đã được định đoạt trước?</span></p>\r\n\r\n<p><span style=\"font-family:inherit; font-size:inherit\">Chỉ có tôi, một người ngoài cuộc nhìn cái cách họ chia tay nhau nhe nhàng và bình thản đến bật khóc.</span></p>\r\n\r\n<p><span style=\"font-family:inherit; font-size:inherit\">Tôi thực sự rất yêu họ.</span></p>', '5.jpg', '2017-03-28 09:39:20', '2017-03-28 09:39:20', 3, 7),
(13, 'Người Cô Độc', '<p><strong>Nếu bạn đang tìm một cuốn tiểu thuyết nóng bỏng, đang muốn đọc về những va chạm xác thịt, hay muốn nghe những câu chuyện ly kì về thế giới thứ 3, thì tôi khuyên bạn hãy bỏ cuốn sách này xuống -&nbsp;Người cô độc là mọi thứ trừ sự trần tục</strong></p>', '<p><strong>Nếu bạn đang tìm một cuốn tiểu thuyết nóng bỏng, đang muốn đọc về những va chạm xác thịt, hay muốn nghe những câu chuyện ly kì về thế giới thứ 3, thì tôi khuyên bạn hãy bỏ cuốn sách này xuống -&nbsp;Người cô độc là mọi thứ trừ sự trần tục.</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"http://www.truyenngan.com.vn/images/quanthanh/tuan6/nguoi_co_doc.jpg\" style=\"border:0px; display:block; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:inherit; margin:0px auto; max-width:90%; padding:0px; vertical-align:baseline\" /></strong></p>\r\n\r\n<p>Khi&nbsp;Người Cô Độc&nbsp;được xuất bản lần đầu tiên năm 1964, nó đã gây ra một cơn chấn động ở Mỹ bởi bức chân dung sinh động của một người trung niên đồng tính, chân thực và cảm động.</p>\r\n\r\n<p>Nó cũng được đánh giá là cuốn tiểu thuyết tiêu biểu dành cho cộng đồng người đồng tính; được xếp vào hạng mục 99 tiểu thuyết viết bằng tiếng Anh hay nhất kể từ năm 1939; được chuyển thể thành phim, với sự tham gia của Colin Firth - người được đề cử giải Oscar ở hạng mục Nam diễn viên chính xuất sắc nhất cho vai diễn này - cùng nữ minh tinh Julianne Moore.</p>\r\n\r\n<p>Câu chuyện miêu tả lại 24 giờ một ngày bình thường của George - giáo sư người Anh sống ở khu ngoại ô Nam California - đang thích ứng với cuộc sống của mình sau cái chết bất ngờ của người bạn đời – Jim – người đã cùng ông chung sống suốt 16 năm. Ông vật lộn dai dẳng với những thói quen cuộc sống thường nhật. Châm biếm, sâu sắc, vui buồn thất thường, hài hước, và nỗi buồn miên man của một con người đã mất đi lẽ sống, chỉ còn ba phần người, cuốn tiểu thuyết này đã lột tả bộ mặt của cuộc sống một cách sắc sảo.</p>\r\n\r\n<p><em>\"Một trong những cuốn tiểu thuyết đầu tiên và hay nhất về sự vận động giải phóng nhân quyền của người đồng tính hiện đại.\"</em>&nbsp;-&nbsp;Edmund White</p>\r\n\r\n<p><strong>Về tác Giả:</strong></p>\r\n\r\n<p>Christorpher Isherwood&nbsp;(1904-1986) là một tiểu thuyết gia lớn của thế kỷ 20, người có những đóng góp tích cực trong phong trào đấu tranh quyền lợi cho người đồng tính, tác giả của hơn 40 tác phẩm nổi tiếng trong những năm giữa thế kỷ 20.</p>\r\n\r\n<p><em>\"Isherwood là nhà viết văn xuôi tiếng Anh tuyệt vời nhất.\"</em>&nbsp;-&nbsp;Gore vidal</p>\r\n\r\n<p>Cùng đón đọc cuốn sách sắp phát hành trong tháng 10 với văn phong tuyệt vời để cảm nhận sự khác biệt của những phong cách văn chương tiếng Anh kinh điển!</p>', '7.jpg', '2017-03-28 09:40:28', '2017-03-28 09:40:28', 4, 7),
(14, 'Chưa hết những câu chuyện tình', '<p><strong>Người trẻ thì hay viết những câu chuyện tình. Ừ thì những chuyện tình có bao giờ kết thúc. Họ còn trẻ, còn yêu và còn viết</strong></p>', '<p><strong>Tập truyện ngắn của 8 tác giả 8X với những cái tên quen thuộc: Yến Linh, Phan Anh, Lưu Quang Minh, Hồ Huy Sơn, Thủy Anna, Hà Thủy Nguyên, Hà Thanh Phúc, Keng.</strong></p>\r\n\r\n<p><strong>Người trẻ thì hay viết những câu chuyện tình. Ừ thì những chuyện tình có bao giờ kết thúc. Họ còn trẻ, còn yêu và còn viết. Có một điều rất lạ. Khi viết, người ta hay bắt đầu từ dễ đến khó. Dễ ở đây theo thiển ý của tôi là dễ (nhanh) hoặc khó (lâu) ra thành phẩm, còn thành phẩm đó hay dở thế nào ấy lại là chuyện khác. Đấy là người sáng tác thường bắt đầu bằng thơ, sang truyện ngắn, rồi tiểu thuyết. Nhưng các cây bút trẻ lại bắt đầu bằng những cuốn tiểu thuyết. Thậm chí những tác giả đã có mặt trong tập sách này cũng lấy tiểu thuyết làm chủ đạo.</strong></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.truyenngan.com.vn/images/quanthanh/tuan6/chua_het_nhung_cau_chuyen_tinh.jpg\" style=\"border:0px; display:block; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:inherit; margin:0px auto; max-width:90%; padding:0px; vertical-align:baseline\" /></p>\r\n\r\n<p>Những truyện ngắn được tập hợp trong cuốn sách này hầu hết đều là những câu chuyện tình, tuy nhiên, với những phong cách viết khác nhau, dù sắc thái tương đối trùng khớp. Truyện ngắn của Yến Linh thường mang sắc thái tự sự với những dòng ý thức kéo dài. Không khí u uất của nỗi chết chóc lẩn quất từ dòng đầu đến dòng cuối của \"Ván cờ trong đêm\", \"Vọng khúc\", mặc dù những nhân vật của nó còn rất trẻ. Điều này cũng có điểm chung với các truyện ngắn của Keng, Hà Thủy Nguyên và Thủy Anna. Khi không tìm cách giải quyết được vấn đề thì người ta nghĩ đến cái chết, dù chỉ là ý nghĩ thoảng qua. Nhưng thậm chí từ trong sự mọc rêu của nỗi buồn và cái chết thì người vẫn thấy cả nhân vật, cả tác giả, cả tư duy, cảm xúc và câu chữ vẫn còn rất trẻ, hoặc chí ít cũng vương vấn chút thơ ngây. Bởi người già, họ nghĩ về cái chết theo một cách khác, ít nhiều không hồn nhiên được đến thế.</p>\r\n\r\n<p>Những đô thị lớn là Sài Gòn và Hà Nội thường được bắt gặp trong truyện của người viết trẻ, với những quán cà phê quen thuộc và không khí công sở, những căn nhà thuê gò bó quây kín nỗi cô đơn của họ. Không gian trong các câu chuyện vì thế đôi khi tan loãng lên tận Đà Lạt và Sapa, như trong \"Có khi, trên cõi mù sương\" (Yến Linh) hay \"Bàn tay cầm mây\" (Hồ Huy Sơn). Những cuộc tình một đêm hoặc ngắn ngủi rồi vẫn cứ diễn ra: \"Người cũ, người cũ hơn và tình một đêm\", \"Như là được yêu\" (Phan Anh), \"Cánh bướm mong manh\" (Keng), \"Cô gái thích nghe Khúc Thụy Du\" (Hồ Huy Sơn), \"Sẽ không điều gì làm em khóc được, trừ anh\", \"Ngang qua một giấc mơ\" (Hà Thanh Phúc).</p>\r\n\r\n<p>Vì những người trẻ lúc nào cũng vội, vội cả yêu. Hoặc giả những cuộc tình ngắn ngủi ấy mang lại cảm hứng tiếc nuối, day dứt mà người trẻ nào cũng trải qua. Họ yêu và chia tay nhau bằng những lý do đơn giản nhưng lại có phần khó hiểu. Tình yêu có vẻ như vẫn thế. Họ cứ thích cố làm đau khổ nhau, hoặc tự làm mình đau khổ. Họ thử thách nhau, rồi sau đó, có thể, họ chết. Họ thử chung sống, thử chia tay vì khi còn trẻ, mọi sự đều chỉ là những trải nghiệm. Họ cũng thường mơ những giấc mơ đầy ám ảnh và khao khát. Đó là khi họ thoát khỏi thế giới hiện thực chưa bao giờ được như ước muốn.</p>\r\n\r\n<div id=\"AbdVideoInPagePlayerWrapper\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%; height: 344px; overflow: hidden; width: 610px;\">\r\n<div id=\"AbdVideoInPagePlayer\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%; position: fixed; width: 300px; height: 170px; left: 0px; bottom: 0px; overflow: hidden; z-index: 2147483647 !important;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Giữa những câu chuyện ấy là cuộc sống khốc liệt nơi phố thị của những người đã lạc chốn thị thành như \"Một tháng, hai mươi tám ngày\" (Hồ Huy Sơn), \"Những vai diễn\" (Hà Thanh Phúc) và một truyện ngắn theo tôi là khá thành công về chủ đề này là \"Tháng ngày lặng lẽ\" (Keng). Với giọng văn đẹp và câu chuyện, dù còn chút gì khiên cưỡng nhưng cảm xúc rất chân thật của người viết đã kéo được người đọc vào không khí cô tịch, chết chóc, bất lực và hoang hoải của một gia đình sống bên lề xã hội.</p>\r\n\r\n<p>Bên cạnh giọng văn tự sự của Yến Linh, Hà Thanh Phúc, văn trần thuật của Phan Anh, Thủy Anna, Hồ Huy Sơn, thì bút pháp huyền ảo cũng là một thế mạnh của nhiều cây bút trẻ. Ngoài \"Con mèo của Karen\" (Yến Linh), \"Say mộng\" (Keng) thì tác giả Hà Thủy Nguyên và Lưu Quang Minh thường ưu tiên sử dụng bút pháp này. Hà Thủy Nguyên từng nổi tiếng với những tiểu thuyết kiếm hiệp giả tưởng nên không khó gì để nhận thấy trong \"Bên kia cánh cửa\", cô đã kể một câu chuyện tình giữa hai thế giới âm dương trong thời đại công nghệ thông tin. Đối với Lưu Quang Minh thì hầu hết các truyện ngắn mà tôi được đọc đều đi theo lối này.</p>\r\n\r\n<p>Cả bốn truyện ngắn của Lưu Quang Minh trong tập đều miêu tả thế giới pha trộn giữa thực và ảo. Chúng tạo nên những cái kết bất ngờ thú vị cho người đọc. Không khí hiện thực của \"Điều bí mật\" kéo dài từ đầu đến gần cuối truyện để rồi người đọc ngỡ ngàng vì những hư thực đã làm tan biến nỗi âu lo đè nặng ngay từ tên truyện. Ngược lại, ở \"Những tâm hồn đồng điệu\", cái ảo lại kéo dài từ đầu tới gần cuối truyện và kết thúc, cái thực khiến người đọc bỗng dưng nhận ra có chính mình ở đó. Cách lý giải về tình yêu của Lưu Quang Minh trong \"Tận thế\", \"Điều bí mật\", \"Mắt và tai\" cũng rất khác những tác giả còn lại trong tập sách. Những cây bút trẻ phía Nam, thường đi theo lối viết hiện đại: trừu tượng, siêu thực, huyền ảo, trong khi các cây bút phía Bắc thường viết theo lối cổ điển. Xưa nay vẫn thế. Và Lưu Quang Minh là một trong số đó.</p>\r\n\r\n<p>Nhưng đọc hầu hết những câu chuyện tình trong tập sách, tôi cứ thấy có chút gì đó... hơi \"giả\". Hay phải chăng đấy là những câu chuyện tình trong mơ? Mơ thì thực sao được. Chuyện tình mà cứ trần tục lại chẳng phải đáng chán lắm sao. Thôi thì người đọc hãy cứ mơ một giấc mơ dài. Và có phải chính vì lý do \"thoát trần\" đó mà các tác giả này đã trở nên rất được ưa thích trong cộng đồng những người Việt trẻ.</p>\r\n\r\n<p>Và rồi, vẫn chưa bao giờ hết những câu chuyện tình.</p>', '8.jpg', '2017-03-28 09:41:53', '2017-03-29 01:15:03', 4, 7),
(18, 'Yêu hay là không', '<p><em>Lưu ý: Truyện được đưa lên với mục đích chia sẻ phi lợi nhuận trong cộng đồng yêu thích tiểu thuyết, đồng thời giúp đỡ các bạn không</em></p>', '<p>&nbsp;</p>\r\n\r\n<p>Cuộc đời, chẳng phải là nơi bằng phẳng. Cuộc đời, muôn nẻo đi về. Do dự một lần, ta sẽ mãi mãi lạc mất nhau. Hãy cứ sống và yêu khi có thể bởi cuộc đời này liệu bao lần được tự hỏi \"Yêu hay là không?...\"</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Yêu hay là không\" src=\"http://www.truyenngan.com.vn/images/linhbp/2016-tieu-thuyet/yeu-hay-khong.jpg\" style=\"border:0px; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:inherit; margin:0px; max-width:90%; padding:0px; vertical-align:baseline; width:500px\" /></p>\r\n\r\n<p><em>Lưu ý: Truyện được đưa lên với mục đích chia sẻ phi lợi nhuận trong cộng đồng yêu thích tiểu thuyết, đồng thời giúp đỡ các bạn không có điều kiện đọc truyện do giới hạn về ngôn ngữ, địa phương, hay điều kiện tìm mua sách.... có thể tiếp cận tác phẩm.</em></p>\r\n\r\n<p><em>Truyenngan.com.vn không chịu trách nhiệm về bản quyền tác giả cũng như bản quyền chuyển ngữ. Truyenngan.com.vn tôn trọng các dịch giả, cộng đồng yêu thích tiểu thuyết cũng như những bạn đã biên tập, gõ lại truyện và tạo ebook tại các nguồn truyện lớn như e-thuvien, Stent, tangthuvien... và chủ nhân của các wordpress cá nhân khác bằng cách đề tên dịch giả, biên tập và người làm ebook.</em></p>', '5.jpg', '2017-03-28 09:51:28', '2017-03-29 01:18:28', 6, 9),
(19, 'Tiểu thuyết kim dung', '<p><em>Liên thành quyết</em><span style=\"color:rgb(33, 25, 34); font-family:tahoma,geneva,sans-serif\">&nbsp;là câu chuyện kể về chàng trai Địch Vân thật thà, chất phác, chưa từng trải việc đời, chỉ vì có cô sư muội Thích</span></p>', '<p><strong>Giới thiệu:</strong></p>\r\n\r\n<p><em>Liên thành quyết</em>&nbsp;là câu chuyện kể về chàng trai Địch Vân thật thà, chất phác, chưa từng trải việc đời, chỉ vì có cô sư muội Thích Phương xinh đẹp nên chàng đã trở thành nạn nhân của Vạn Khuê. Hắn đã lập mưu hãm hại chàng để chiếm đoạt Thích Phương. Địch Vân bị vu oan cho tội hãm hiếp và ăn trộm, bị chặt đứt 5 ngón tay, bị xuyên xương bả vai và bị tống vào lao tù.</p>\r\n\r\n<p>Trong tù, Địch Vân làm quen được với Đinh Điển, một cao thủ đệ nhất võ công đương thời, người đang nắm giữ bí mật về Liên thành quyết (một bộ kiếm pháp vô địch và là bản đồ chỉ đến một kho báu). Đinh Điển đã truyền võ công và nói bí mật về kho báu cho Địch Vân biết. Sau đó cả hai cùng vượt ngục. Trên đường chạy trốn, Đinh Điển chết vì bị trúng độc, còn Địch Vân lại bị rơi vào một nỗi oan khác: Thiên hạ tưởng chàng là \"dâm tăng\" và đuổi giết cho kỳ được.</p>\r\n\r\n<p>Địch Vân bị dồn vào đường cùng. Tình cờ do lở tuyết, chàng và cô nương xinh đẹp Thủy Sinh bị tuyết chôn vùi trong một tuyệt cốc ở gần Tây Tạng trong suốt một mùa đông. Mãi đến mùa xuân khi tuyết tan, Địch Vân và Thủy Sinh mới thoát ra khỏi cái nhà tù - tuyệt cốc ấy. Lúc đó, Địch Vân đã luyện được võ công vô địch và chàng bắt đầu hành trình trả thù. Còn Thủy Sinh thì bị người yêu là chàng Uông Khiếu Phong nghi ngờ, ruồng rẫy vì đã ở chung với \"dâm tăng\" mấy tháng trời trong tuyệt cốc (dù giữa Địch Vân và Thủy Sinh hoàn toàn trong sáng và cũng nhờ vậy, Thủy Sinh mới biết Địch Vân là một người trung hậu, thật thà, chịu nhiều oan khuất).</p>\r\n\r\n<p>Về đến chốn cũ, Địch Vân tan nát cõi lòng khi thấy Thích Phương đã trở thành vợ của Vạn Khuê, nhưng vì hạnh phúc của sư muội, Địch Vân không nỡ giết kẻ đã hãm hại mình. Thích Phương khi đã biết rõ sự thật, dù còn yêu Địch Vân nhưng nàng vẫn không thể bỏ rơi chồng. Nàng đi cứu Vạn Khuê và bị chính hắn giết chết.</p>\r\n\r\n<p>Địch Vân đi tìm kho báu để giúp thiên hạ theo lời trăng trối của Đinh Điển. Tại đó, Địch Vân đã suýt chết khi bị sư phụ thuở thiếu thời của chàng là Thích Trường Phát (cha của Thích Phương, một người mà Địch Vân luôn kính trọng) đâm lén một đao để hòng chiếm đoạt kho báu một mình.</p>\r\n\r\n<div id=\"AbdVideoInPagePlayerWrapper\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%; height: 344px; overflow: hidden; width: 610px;\">\r\n<div id=\"AbdVideoInPagePlayer\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%; position: fixed; width: 300px; height: 170px; left: 0px; bottom: 0px; overflow: hidden; z-index: 2147483647 !important;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Quá thất vọng trước nhân tình thế thái, người yêu thì đã chết, sư phụ thì trở mặt trước mãnh lực của bạc vàng, Địch Vân lang thang vô định và bước chân đã xui khiến chàng trở về tuyệt cốc, nơi không bị người đời quấy rối.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Liên thành quyết\" src=\"http://www.truyenngan.com.vn/images/linhbp/2015-tieu-thuyet/lien-thanh-quyet.jpg\" style=\"border:0px; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:inherit; margin:0px; max-width:90%; padding:0px; vertical-align:baseline; width:500px\" /></p>\r\n\r\n<p><em>Lưu ý: Truyện được đưa lên với mục đích chia sẻ phi lợi nhuận trong cộng đồng yêu thích tiểu thuyết, đồng thời giúp đỡ các bạn không có điều kiện đọc truyện do giới hạn về ngôn ngữ, địa phương, hay điều kiện tìm mua sách.... có thể tiếp cận tác phẩm.</em></p>\r\n\r\n<p><em>Truyenngan.com.vn không chịu trách nhiệm về bản quyền tác giả cũng như bản quyền chuyển ngữ. Truyenngan.com.vn tôn trọng các dịch giả, cộng đồng yêu thích tiểu thuyết cũng như những bạn đã biên tập, gõ lại truyện và tạo ebook tại các nguồn truyện lớn như e-thuvien, Stent, tangthuvien... và chủ nhân của các wordpress cá nhân khác bằng cách đề tên dịch giả, biên tập và người làm ebook.</em></p>', '11.jpg', '2017-03-28 09:53:44', '2017-03-28 09:53:44', 4, 10),
(20, 'LỤC TIỂU PHỤNG - CỔ LONG', '<p><span style=\"color:rgb(33, 25, 34); font-family:tahoma,geneva,sans-serif\">Lục Tiểu Phụng là một con người. Con người này không ai quên được, không bao giờ quên được. Cuộc đời chàng đầy tính truyền kỳ, và chàng đã gặp không biết bao nhiêu kỳ nhân kỳ sự. Bất cứ lúc nào và bấ</span></p>', '<div class=\"breadcrumbs\" style=\"margin: 0px 0px 0px -9px; padding: 9px 0px 10px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; line-height: inherit; font-family: Tahoma, Geneva, sans-serif; vertical-align: baseline; max-width: 100%; width: 628px; float: left; background: rgb(227, 29, 126); color: rgb(255, 255, 255); position: absolute; top: 0px; z-index: 1;\">\r\n<div class=\"padding\" style=\"margin: 0px; padding: 0px 10px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%;\"><a class=\"pathway\" href=\"http://www.truyenngan.com.vn/\" style=\"color: rgb(255, 255, 255); text-decoration: none; padding: 0px 2px;\">Home</a>&nbsp;&gt;&nbsp;<a class=\"pathway\" href=\"http://www.truyenngan.com.vn/tieu-thuyet.html\" style=\"color: rgb(255, 255, 255); text-decoration: none; padding: 0px 2px;\">Tiểu Thuyết</a>&nbsp;&gt;&nbsp;<a class=\"pathway\" href=\"http://www.truyenngan.com.vn/tieu-thuyet/kiem-hiep.html\" style=\"color: rgb(255, 255, 255); text-decoration: none; padding: 0px 2px;\">Kiếm Hiệp</a>&nbsp;&gt;&nbsp;<span style=\"font-family:inherit; font-size:inherit\">Lục Tiểu Phụng - Cổ Long</span></div>\r\n</div>\r\n\r\n<div class=\"viewcontent-page\" style=\"margin: 0px; padding: 60px 0px 10px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; line-height: inherit; font-family: Tahoma, Geneva, sans-serif; vertical-align: baseline; max-width: 100%; width: 610px; float: left; color: rgb(33, 25, 34);\">\r\n<h1>LỤC TIỂU PHỤNG - CỔ LONG</h1>\r\n\r\n<div class=\"maincontent\" style=\"margin: 10px 0px; padding: 10px 0px 60px; border-width: 0px 0px 2px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(227, 29, 126); border-left-color: initial; border-image: initial; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%; width: 610px; float: left;\">\r\n<p><strong>Giới thiệu:</strong></p>\r\n\r\n<p>Lục Tiểu Phụng là một con người. Con người này không ai quên được, không bao giờ quên được. Cuộc đời chàng đầy tính truyền kỳ, và chàng đã gặp không biết bao nhiêu kỳ nhân kỳ sự. Bất cứ lúc nào và bất cứ nơi đâu chàng cũng có thể kể cho bạn nghe những câu chuyện cổ quái.</p>\r\n\r\n<p>Hùng Mỗ Mỗ đột nhiên xuất hiện giữa đám sương mù dày đặc chẳng khác gì u linh quỷ mỵ. Lưng mụ còng gập xuống, tưởng như đang bị một tảng đá lớn vô hình đè nặng đến gẫy xương sống. Cứ đến độ trăng tròn là mụ lại muốn giết người.</p>\r\n\r\n<p>Nhà sư chất phác rất biết điều, ngồi vào tận góc thuyền, cứ cúi đầu nhìn xuống chân mình. Chân nhà sư dơ quá, lại đi đôi giày cỏ rách nát. Ngay lúc cường bạo nhảy xuống thuyền, nhà sư cũng không ngẩng lên nhìn lần nào. Nhà sư chỉ nghe những người ngồi trên thuyền la hoảng, rồi nghe tiếng bốn người nhảy xuốg thuyền. Sau nữa là tiếng quát tháo của bọn cường đạo: Chúng ta là hảo hán ở Thủy Xà Bang, trước nay chỉ lấy tiền chứ không giết người. Các ngươi bất tất phải sợ hãi, cứ bỏ hết vàng bạc trong mình ra là không sao cả. Sáng hôm sau, có người phát giác Thủy Xà Bang từ trên xuống dưới có mười tám tên hảo hán đột nhiên chết sạch trong sào huyệt. Bọn này đều chết tự nhiên, đã chẳng bị thương cũng không trúng độc. Không một ai khám phá ra bọn chúng chết vì lý do gì.</p>\r\n\r\n<p>Tây Môn Xuy Tuyết thích thổi máu chứ không thổi tuyết. Kiếm của y thường dính máu.</p>\r\n\r\n<p>Hồng Đào có biệt hiệu là Thiểm Điện Đao. Nếu đao pháp của Thiểm Điện Đao mà không thực sự nhanh như chớp thì Nhất đao chấn cửu châu Triệu Cương đã không chết dưới đao của hắn.</p>\r\n\r\n<div id=\"AbdVideoInPagePlayerWrapper\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%; height: 344px; overflow: hidden; width: 610px;\">\r\n<div id=\"AbdVideoInPagePlayer\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; max-width: 100%; position: fixed; width: 300px; height: 170px; left: 0px; bottom: 0px; overflow: hidden; z-index: 2147483647 !important;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hoa Mãn Lâu yêu thích hoa tươi rất mãnh liệt, cũng như y yêu mến tất cả mọi loại sinh mạng vậy.</p>\r\n\r\n<p>Thượng Quan Phi Yến là một tên ăn cắp vặt. Trước nay chưa bao giờ lấy cắp của người tử tế, mà chỉ chuyên trộm đồ của bọn cường bạo.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Lục tiểu phụng\" src=\"http://www.truyenngan.com.vn/images/linhbp/2015-tieu-thuyet/luc-tieu-phung.jpg\" style=\"border:0px; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:inherit; margin:0px; max-width:90%; padding:0px; vertical-align:baseline; width:500px\" /></p>\r\n\r\n<p><em>Lưu ý: Truyện được đưa lên với mục đích chia sẻ phi lợi nhuận trong cộng đồng yêu thích tiểu thuyết, đồng thời giúp đỡ các bạn không có điều kiện đọc truyện do giới hạn về ngôn ngữ, địa phương, hay điều kiện tìm mua sách.... có thể tiếp cận tác phẩm.</em></p>\r\n\r\n<p><em>Truyenngan.com.vn không chịu trách nhiệm về bản quyền tác giả cũng như bản quyền chuyển ngữ. Truyenngan.com.vn tôn trọng các dịch giả, cộng đồng yêu thích tiểu thuyết cũng như những bạn đã biên tập, gõ lại truyện và tạo ebook tại các nguồn truyện lớn như e-thuvien, Stent, tangthuvien... và chủ nhân của các wordpress cá nhân khác bằng cách đề tên dịch giả, biên tập và người làm ebook</em></p>\r\n</div>\r\n</div>', '3.jpg', '2017-03-28 09:54:46', '2017-03-28 09:55:21', 3, 10),
(24, 'Truyện cười học sinh và đôi nét cần biết', 'Những mấu truyện cười thật vui trên lớp học về những học sinh lém lĩnh làm cho người đọc phải cười đến lăn ra đất. Những câu nói hồn nhiên, vô tư và nhí nhảnh của các cậu học trò này đã đóng góp cho kho truyện cười những câu chuyện thật hài hước', '<h2><a href=\"http://www.truyencuoihay.vn/truyen-cuoi-hoc-sinh/khong-ai-hoc-ca\" style=\"margin: 0px; padding: 4px 10px 8px 0px; font-size: 18px; vertical-align: baseline; background: transparent; color: rgb(92, 91, 91); text-decoration: none; display: block; height: auto; line-height: 1.3em; overflow: hidden;\">Không ai học cả</a></h2>\r\n\r\n<div class=\"description\" style=\"margin: 0px; padding: 0px 0px 0px 10px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background: transparent; color: rgb(85, 85, 85); line-height: 1.5; font-family: arial, helvetica, sans-serif;\">\r\n<p>Tý vừa khóc mếu máo vừa chạy về nhà, mách với bố:</p>\r\n\r\n<p>- Thầy giáo trù đập con quá, bố ạ. Hôm nào thầy cũng gọi con lên trả bài. Thầy cố tình chọn những câu thật khó để con không trả lời được thì thầy phạt. Hôm nay con lại bị phạt cầm tai đứng ở cuối lớp.</p>\r\n\r\n<p>- Bố Tý tức lắm. Hôm sau ông ta dẫn Tý, hầm hầm vào gặp thầy: Tôi nghe cháu nó nói thầy trù đập nó ghê lắm. Tại sao thầy lại đối xử với con tôi như vậy?</p>\r\n\r\n<p>- Tôi trù đập con ông hồi nào đâu, nhưng ông xem, hôm nào tôi bảo nó lên bảng trả bài, nó cũng không trả lời được, kể cả câu hỏi dễ nhất.</p>\r\n\r\n<p>- Tôi lại nghe cháu nó nói thầy toàn chọn câu khó thôi. Đâu, thầy cho tôi một ví dụ xem nào.</p>\r\n\r\n<p>- Này nhé, hôm qua tôi hỏi nó tướng Trần Hưng Đạo chết hồi nào mà nó có trả lời được đâu.</p>\r\n\r\n<p>- Bố Tý ngẫm nghĩ một lúc, rồi đáp: Thôi, thầy cũng thông cảm cho cháu. Gia đình tôi là gia đình làm ăn buôn bán, lâu lâu coi báo thì xem tin tức chứ ai đi đọc cáo phó làm gì.</p>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"fb-like fb_iframe_widget\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background: transparent; display: inline-block; position: relative; color: rgb(92, 91, 91); font-family: arial, helvetica, sans-serif;\">&nbsp;</div>', '5.jpg', '2017-03-29 01:49:27', '2017-03-29 01:49:27', 7, 11),
(25, 'Hình ảnh 8-3 hài hước nhất – ảnh chế bá đạo', 'Hãy cười thả ga với hình ảnh chế hài hước, bá đạo nhất về ngày quốc tế phụ nữ 8-3 này nhé. Hình ảnh 8-3 hài hước vừa buồn cười, vừa ngầm nhắn nhủ răn đe với phái mạnh rằng mai là ngày 8/3 – ngày đặc biệt dành cho một nửa thế giới', '<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">Phụ nữ có tiết kiệm không?</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Người ta nói rằng: “Phụ nữ luôn tiết kiệm trong sự hoang phí!”. Điều đó không hẳn lúc nào cũng đúng, xin đơn cử một ví dụ: Khi chọn đồ tắm, đồ bơi, phụ nữ bao giờ cũng có xu hướng chọn loại ít… tốn vải nhất. Rõ ràng trong việc này phụ nữ đã cực kỳ tiết kiệm đấy chứ!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">*</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">* *</span><br />\r\n<span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">Phát biểu nhỡ mồm ngày 8-3</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Một công ty nọ nhân dịp&nbsp;<a href=\"http://thuthuattienich.com/tag/8-thang-3/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; box-sizing: border-box; color: rgb(5, 5, 240); transition: all 0.2s ease-in-out;\">8-3</a>&nbsp;mời các chị em phụ nữ mặc áo dài, trang điểm muôn phần tươi đẹp, lên hội trường tập trung một hàng ngang để sếp tặng quà cho từng người một. Sau khi trao quà xong ông này “phát một cái biểu” như sau: “Nhân dịp quốc tế phụ nữ, mong chị em luôn tạo ra… hưng phấn cho các anh em say sưa làm việc”.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">*</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">* *</span><br />\r\n<span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">Những món quà 8-3 truyền thống</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Còn tại một công ty khác, đều như vắt chanh, 8-3 năm nào sếp cũng tặng các chị em phụ nữ trong công ty những lọ sữa tắm. Chị em ngán ngẩm lắm bèn ca cẩm ngầm với tay trợ lý của sếp. Quả nhiên 8-3 năm đó các chị em được tặng món quà rất khác, nhỏ nhắn, xinh xắn và được gói trong những hộp vuông nho nhỏ có mùi thơm. Hồi hộp mọi người mở quà, thì ra đó chính là những bánh… xà bông tắm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">*</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">* *</span><br />\r\n<span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">Hậu họa ngày 8-3</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Chiều 8-3, anh Piter người Úc tỉnh dậy sau một giấc ngủ nướng từ 8 giờ sáng. Theo thói quen anh đi vào nhà vệ sinh, lúc trở ra anh bỗng thấy “cái thứ làm nên người đàn ông” của anh đột nhiên bị bỏng rát rồi chuyển sang tấy đỏ. Hai mắt trở nên rất khó nhìn và nước mắt giàn dụa. Piter vô cùng lo lắng, không hiểu chuyện gì đang xảy ra. Đem chuyện kể với vợ, bà xã của Piter tỏ ra rất giận dữ, cô quả quyết rằng anh đã vụng trộm với một cô ả rẻ tiền nào đó và rước bệnh vào người. Piter thề sống thề chết rằng không hề có chuyện ấy. Vợ Piter nói: “Nếu vậy thì chúa đã trừng phạt anh vì một tội lỗi gì đó”. Sau khi tự suy xét, kiểm điểm bản thân, Piter thấy chỉ có một khả năng: Anh quên (cố tình quên?) mua quà 8-3 tặng vợ. Kìm nén cảm giác bỏng rát, Piter chạy ra siêu thị gần đó mua quà tặng vợ để chuộc lỗi với hy vọng rằng chúa sẽ tha tội. Kỳ lạ thay sau khi làm việc đó, các triệu chứng bỏng rát dần dần biến mất vài giờ sau đó.&nbsp;</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Mãi về sau Piter mới biết rằng, do anh không mua quà 8-3 nên chị vợ ức quá đã lén lúc anh ngủ bôi ớt tươi đậm đặc lên 10 đầu ngón tay của anh cho bõ ghét. Sau khi Piter đi vào nhà vệ sinh thì chuyện nói trên xảy ra.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">Thỏa thuận</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Tối&nbsp;<strong>8/3</strong>, sau một ngày vợ chồng đổi vai trò trong nhà như giao ước. Lưng đau như dần, tay bợt bạt, chàng ngồi đần mặt chờ đợi. Đến khuya, không thấy chàng vào giường, nàng đến bên thỏ thẻ:</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Anh yêu, chúng ta còn chờ gì nữa mà không cùng nhau đặt dấu chấm cho một ngày tốt đẹp.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Chàng nhõng nhẽo:</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Thế em không định… bế anh vào giường sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">Ăn không?</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Ngày 8/3 Một chàng sinh viên chở bạn gái trên một chiếc xe đạp. Đang</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">đi, bỗng nhiên chàng thắng lại cái “ké…é….ét” ngay trước một quán</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">chè rồi quay ra sau hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Ăn không???</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Nàng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Ăn!!!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Chàng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Có thế chứ! Bộ thắng này mới thay hồi sáng đó! Nói rồi, chàng tiếp tục đạp xe đi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">GHEN VỢ</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Anh nọ mới cưới vợ lại có tính hay ghen .Hôm nào ở nhà không sao , còn tối nào đi vắng anh ta đều lấy tro rải ra nền nhà , từ cửa buồng cho đến giường vợ .</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Một hôm đi thăm bạn định ngủ lại , nhưng nghĩ sao anh ta lại về , dù đã quá nửa đêm và leo lên giường ngủ một mạch .</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Sáng ra nhìn thấy dấu chân , nghi vợ ngoại tình , chẳng nói chẳng rằng anh ta lôi vợ ra đánh .Vừa đánh vừa hỏi :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Dấu chân thằng nào đây ?</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Lúc đầu cô vợ còn im , sau đau quá mới nói :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Dấu chân thằng chó đấy ! Đi thăm bạn còn mò về đây ngủ .</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">THÀNH THẬT</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Một cô gái nói với chồng sắp cưới :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Trước khi cưới em sẽ thú thật những chuyện lăng nhăng của em trước đây .</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Anh chồng ngạc nhiên :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Hai hôm trước em đã chẳng nói hết là gì ?</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Nhưng đó chỉ là chuyện xảy ra trước hai hôm đó.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">LÝ DO</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Một bà trung niên giàu có vừa lấy một thanh niên vừa trẻ vừa đẹp trai .Bạn bè ngạc nhiên mới hỏi lý do vì sao anh ta lại chịu lấy bà .Bà đáp :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Có gì đâu .Tôi mới 50 tuổi nhưng tôi bảo anh ta là tôi đã 70 .</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">TRINH TIẾT</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Trong bữa tiệc cưới , khi đang ngồi với đám bạn gái , cô dâu nghe thấy chú rể ở bàn bên đánh cuộc với bạn :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Tôi cam đoan cô ấy còn trinh .Tôi đánh cuộc chấp một ăn năm .</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Cô vênh váo nhìn bạn bè vẻ đắc thắng .Nhưng khi có hai người ngồi trong xe cô gắt ầm lên :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Sao anh có thể làm một việc như thế ?Chúng ta vừa mới cưới được một tiếng đồng hồ , vậy mà anh đã phung phí tiền bạc .</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">BẺ BỚT</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Thấy anh chồng mới cưới suốt ngày đánh vợ , hàng xóm nhắc khéo :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Phụ nữ là những bông hoa , không nên vùi dập họ .</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Anh chồng chống chế :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Đâu có ! Tôi chỉ bẻ bớt gai của bông hoa đó thôi .</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">QUÁ CẨN THẬN</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Một bà có thai lần thứ tư , mà lần nào cũng khám cùng một bác sĩ .Biết bà ta chưa cưới , bác sĩ mới khuyên :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Những đứa con bà cần phải có cha .Thế sao bà không…</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Bà nọ ngắt lời :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Nhưng người đó vẫn là cha của các con tôi mà .</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Vậy sao bà vẫn chưa làm đám cưới với ông ấy ? Ông ta không muốn à ?</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Ông ta muốn chứ !Ấy là tại tôi còn lo ngại đó .</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Tại sao ?</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">-Tôi còn chờ xem ông ta có đứng đắn không đã !</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0); font-size:large\"><strong><span style=\"font-family:times new roman,times\">ĐỒNG VỢ ĐỒNG CHỒNG</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Trong phiên toà xử thuận tình ly hôn , vị chánh án hỏi :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Nguyên nhân gì khiến anh chị đồng ý ký vào đơn ly hôn ?</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Người chồng bức xúc nói :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Thưa quý toà , có lần đang ngủ cô ấy bỗng giật mình la to ” Chết rồi ! chồng em về “</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">Người vợ cướp lời :</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:times new roman,times; font-size:medium\">– Khi em la vậy thì anh ấy hoảng hốt chui vội xuống gầm giường trốn .</span></p>', '5.jpg', '2017-03-29 01:52:39', '2017-03-29 01:52:39', 7, 11),
(31, 'Bài viết laravel mới nhất', 'Nôi dung laravel', '<p>Nội dung rất hay!!!!!!!!!1</p>', '10.jpg', '2017-03-30 02:01:08', '2017-03-30 02:01:08', 4, 12),
(32, 'Nếu như trở lại thời tiền sử sinh sống, loại người nhất định sẽ chết không nghi nghờ nữa', '<p>Nếu như trở lại thời tiền sử sinh sống, loại người nhất định sẽ chết không nghi nghờ nữa😰</p>', '<p style=\"text-align:justify\"><strong>Trước nền văn minh nhân loại của chúng ta hiện nay, trên trái đất có nhiều “quái vật” sinh sống. Chúng có thể hình rất lớn và sức mạnh kinh khủng, còn có một số loài có kích thước nhỏ hơn nhưng bản lĩnh săn mồi rất xuất sắc. Bây giờ những “quái vật” này biến thành hình dạng thế nào? Chúng ta cùng xem nhé!</strong></p>\r\n\r\n<p style=\"text-align:justify\">▼ Chim cánh cụt (khoảng 4.5 kg): Tổ tiên là “The water king” sống vào khoảng 36 triệu năm trước, nặng khoảng 59 kg</p>', 'long.jpg', '2017-03-30 02:23:49', '2017-03-30 02:24:39', 4, 11);
INSERT INTO `posts` (`id`, `title`, `summary`, `content`, `images`, `created_at`, `updated_at`, `user_id`, `cate_id`) VALUES
(33, 'Những tình huống lặp đi lặp lại trong phim có bao giờ khiến bạn thắc mắc', '<p>Những tình huống lặp đi lặp lại trong phim có bao giờ khiến bạn thắc mắc</p>', '<p>5 tình tiết hết sức vô lý trong phim kiếm hiệp nhưng 99% không ai nhận ra<br />\r\nIN GIẢI TRÍ</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>kiem-hiep</p>\r\n\r\n<p>&nbsp;<br />\r\nXem phim kiếm hiệp đã nhiều lần rồi, nhưng có bao giờ bạn cảm thấy có gì đó “là lạ” không? Hãy cùng chúng tôi khám phá qua bài viết dưới đây nhé!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>1. Có việc khẩn toàn đi ngựa</p>\r\n\r\n<p><br />\r\n&nbsp;<br />\r\nD6</p>\r\n\r\n<p>Trong các phim kiếm hiệp hầu như phim nào cũng xuất hiện hình ảnh các nhân vật – từ già trẻ gái trai – đều có khả năng bay nhảy đánh nhau trên không, trên nóc nhà hay trên ngọn cây. Nếu là người biết võ công thì đều thông thạo những kỹ năng như phi thân trên những ngọn cây, bay tưng bừng trên ngọn núi, chạy tung tăng qua các mái nhà. Tuy nhiên, khi có việc khẩn như cứu người, báo tin thì thường lựa chọn ngựa chứ không phải bay nhảy tưng bừng như lúc đánh nhau.</p>\r\n\r\n<p>2. Nến thần</p>\r\n\r\n<p><br />\r\n&nbsp;<br />\r\nd7</p>\r\n\r\n<p>Trong phim kiếm hiệp, những cây nến là vật dụng thường xuyên được sử dụng với mục đích chiếu sáng. Tuy nhiên, có điều khá vô lý là nhiều khi chỉ sử dụng một ngọn nến thôi nhưng cả căn phòng lớn đều sáng bừng như bật đèn điện.</p>\r\n\r\n<p>3. Giá trị của 1 lượng bạc</p>\r\n\r\n<p>d8</p>\r\n\r\n<p><br />\r\nTrong phim kiếm hiệp, chi tiết 1 lượng bạc xuất hiện vô cùng thường xuyên. Đặc biệt những nhân vật trong phim kiếm hiệp thường khá “lịch sự’. Chẳng hạn khi đang ăn uống tại một quán ăn nào đấy nhưng có việc gấp hay bị kẻ thù truy đuổi đến nơi thì nhân vật này vẫn vứt lại 1 lượng bạc để trả tiền. Không chỉ đồ ăn mà bất kỳ mọi món đồ trong phim kiếm hiệp đều có giá 1 lượng bạc như rượu, kẹo hồ lô, vải vóc…</p>\r\n\r\n<p>4. Dùng đồ ăn ném vào tử tù</p>\r\n\r\n<p>d9</p>\r\n\r\n<p>Trong nhiều bộ phim kiếm hiệp, cảnh những tử tù bị diễu phố thường xuyên diễn ra. Mỗi lần tử tù bị diễu phố đều nhận được vô số cà chua, trứng, rau cải… Trong khi đó, hầu hết tử tù bị diễu qua những khu vực dân nghèo nhưng không hiểu những dân ở đây lại sẵn sàng mang đồ ăn ra ngoài đường để ném vào tử tù trong khi mình lại đang bị đói . (Ảnh minh họa)</p>\r\n\r\n<p>5. Đầu phim đến cuối phim chỉ mang đúng một bộ đồ</p>\r\n\r\n<p><br />\r\nD10</p>\r\n\r\n<p>Trong truyện kiếm hiệp thông thường các đại hiệp hay đi đến nơi này nơi nọ, đánh nhau từ rừng rậm cho tới sa mạc rồi nhà kho, bùn lầy… Thậm chí có nhiều người còn bế quan luyện công đến hàng tháng trời nhưng không hề bận tâm tắm rửa hay giặt giũ đến 1 lần. Có nhiều người vẫn giữ nguyên bộ quần áo rách, cũ hết từ tập nay sang đến tập khác mặc dù yếu tố thời gian đã thay đổi.</p>', '3.jpg', '2017-03-30 02:27:49', '2017-03-30 02:28:11', 3, 9),
(34, 'Hương Tràm lấy nước mắt khán giả với MV \"Cho em gần anh thêm chút nữa\"', '<p>Một tuần trước ngày chiếu, \"Cho em gần anh thêm chút nữa\" tiếp tục khiến người xem đứng ngồi không yên khi công bố MV nhạc phim đầy cảm xúc qua giọng hát của ca sĩ Hương Tràm</p>', '<p><span style=\"font-family:roboto,arial,sans-serif; font-size:14px\">﻿</span></p>\r\n\r\n<p style=\"text-align:justify\">Cho Em Gần Anh Thêm Chút Nữa là bộ phim của những người trẻ đi tìm&nbsp;<a href=\"http://2sao.vn/yeu-c-aa3/\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(51, 122, 183); text-decoration: none; transition: all 0.4s ease 0s; outline: none !important;\">tình yêu</a>, mục đích sống. Sau cái chết của cha mẹ, Kai (Đình Hiếu) tự chôn vùi tuổi xuân của mình trong nỗi buồn vô hạn. Một ngày nọ, Rin (Jun Vũ) bước vào cuộc đời Kai như con đom đóm nhỏ thắp lại hy vọng sống cho anh. Nhưng rồi bản thân Rin cũng mang một bí mật ẩn sau câu chuyện Kỹ Nữ Máu mà cô thường kể anh nghe. Liệu tình yêu, sự đồng điệu trong tâm hồn của đôi trẻ có thể vượt qua định mệnh nghiệt ngã?</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://2sao.vietnamnetjsc.vn/images/2016/11/25/14/25/1.jpg\" style=\"border:0px; box-sizing:border-box; height:230px; max-width:100%; vertical-align:middle; width:550px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>MV gây xúc động mạnh khi đoạn mở đầu hé lộ tình tiết Kai cầu hôn Rin ngay trước nhà vĩnh biệt</em></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nMV lấy nước mắt người xem khi Rin yêu cầu Kai “ôm chặt em hơn đi, cho em gần anh thêm chút nữa…gần anh thêm chút nữa” khiến người xem vừa ngậm ngùi và tò mò điều gì đang diễn ra với đôi trẻ. Khoảnh khắc Kai ôm Rin vào lòng trước nhà vĩnh biệt để tìm chút bình yên trước cơn giông bão, đó cũng là khi tình yêu thăng hoa, mà cũng là nỗi buồn trào dâng đến xé lòng. Nhưng ngay sau đó, người xem phải vội lau đi nước mắt để cùng tận hưởng cuộc sống vui tươi và tràn ngập tiếng cười của đôi trẻ khi Hương Tràm cất giọng hát.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n“<em>Một chút nhớ anh đấy<br />\r\nMột chút mơ chưa đầy<br />\r\nMột chút thương anh mà xa như khói mây<br />\r\nMột chút ít hơi ấm<br />\r\nMột chút thương âm thầm<br />\r\nMột chút yêu thôi, nằm sâu như sóng ngầm…</em>”&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nTình yêu vốn dĩ là thứ ngọt ngào nhưng có lẽ giai đoạn tuyệt vời nhất vẫn là ở tuổi thanh xuân vì khi đó, hai con tim trao cho nhau tình cảm nồng nhiệt, thuần khiết nhất từ những nụ cười, những khoảnh khắc lãng mạn đến cả những giận hờn vu vơ.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://2sao.vietnamnetjsc.vn/images/2016/11/25/14/26/19.jpg\" style=\"border:0px; box-sizing:border-box; height:231px; max-width:100%; vertical-align:middle; width:550px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://2sao.vietnamnetjsc.vn/images/2016/11/25/14/26/12.jpg\" style=\"border:0px; box-sizing:border-box; height:231px; max-width:100%; vertical-align:middle; width:550px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Tình yêu đẹp của tuổi thanh xuân.</em></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nHạnh phúc tưởng chừng trải dài vô tận nhưng hóa ra nó cũng chỉ mong manh như nguồn sáng của con đom đóm giữa đêm đen. Rồi tình yêu đẹp ấy cũng gặp những sóng gió, cũng có những hiểu lầm, những trăn trở của đôi lứa yêu nhau, và cũng là những giọt nước mắt đầy oán trách giận dữ. Nhưng rồi, mọi chuyện lại đi theo guồng vốn có của nó, đau khổ rồi sẽ hạnh phúc…và MV kết thúc là những nụ cười, những sự đồng cảm, và khuôn mặt rạng lên những hạnh phúc lớn lao.&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<img alt=\"\" src=\"http://2sao.vietnamnetjsc.vn/images/2016/11/25/14/27/27.jpg\" style=\"border:0px; box-sizing:border-box; height:231px; max-width:100%; vertical-align:middle; width:550px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://2sao.vietnamnetjsc.vn/images/2016/11/25/14/27/30.jpg\" style=\"border:0px; box-sizing:border-box; height:233px; max-width:100%; vertical-align:middle; width:550px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Rồi hạnh phúc sẽ quay trở về sau một thời gian dài kiếm tìm</em></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<em>Trái tim em và dòng máu nóng để yêu anh<br />\r\nGiấc mơ ơi ở lại bên tôi đừng tan nhanh<br />\r\nNgày mai thức giấc thấy giữa lồng ngực là hình bóng ấy<br />\r\nNắm tay em đừng để em đi, đừng để em đi<br />\r\nLỡ sinh ra là để yêu nhau chẳng rời xa đâu<br />\r\nBình yên ở đây. Ở đây… chẳng thể rời xa</em>.”</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nTác giả ca khúc Tăng Nhật Tuệ chia sẻ bản thân rất đồng cảm với câu chuyện tình trong phim. Cặp đôi Rin - Kai đến với nhau vì đều có bất hạnh trong cuộc sống riêng, qua đó tìm thấy sự đồng điệu trong tâm hồn. Đó không chỉ là tình yêu đơn thuần mà còn là khát vọng sống, dòng máu nóng của những người trẻ. Giọng hát mượt mà, đầy nội lực của quán quân Giọng Hát Việt gây cảm xúc động mạnh mẽ cho người xem và đây cũng là lần đầu tiên Hương Tràm hát&nbsp;<a href=\"http://2sao.vn/nhac-phim-c-abs/\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(51, 122, 183); text-decoration: none; transition: all 0.4s ease 0s; outline: none !important;\">nhạc phim</a>&nbsp;cho một tác phẩm&nbsp;<a href=\"http://2sao.vn/phim-c-aaj/\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(51, 122, 183); text-decoration: none; transition: all 0.4s ease 0s; outline: none !important;\">điện ảnh</a>&nbsp;Việt Nam. “Hương Tràm cũng chỉ vừa bước qua tuổi 21 nên hiểu được tâm hồn của những người trẻ trong phim nên “phiêu” theo từng nốt nhạc mỗi lần thu âm, từng lời như chạm vào trái tim của mình” – nữ ca sĩ chia sẻ.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://2sao.vietnamnetjsc.vn/images/2016/11/25/14/27/11.jpg\" style=\"border:0px; box-sizing:border-box; height:231px; max-width:100%; vertical-align:middle; width:550px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://2sao.vietnamnetjsc.vn/images/2016/11/25/14/28/14.jpg\" style=\"border:0px; box-sizing:border-box; height:235px; max-width:100%; vertical-align:middle; width:550px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Hương Tràm xúc động khi trình bày ca khúc của phim</em></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nMV đặc biệt này như món quà của ê-kíp Cho Em Gần Anh Thêm Chút Nữa gửi tặng những người đã yêu và đang yêu, những người đang hạnh phúc hay cả những ai đang phải cách xa người thương. Sau khoảng thời gian chờ đợi, khán giả sẽ trải qua những cung bậc tình yêu cùng Rin và Kai vào ngày 2 tháng 12 sắp tới.</p>', 'ht.jpg', '2017-03-30 02:44:00', '2017-03-30 02:45:15', 7, 13),
(35, 'lore', 'qưewdefdgh', '<p>qưedefgthng</p>', '8.jpg', '2017-03-30 03:55:20', '2017-03-30 03:55:20', 4, 11),
(36, '1244444444444444444', '444444444444444444444', '<p>444444444444444444444</p>', '', '2017-03-30 04:23:03', '2017-03-30 04:23:03', 4, 6),
(37, 'Bài 04: Mô hình thực thể mối kết hợp (ER)', 'Tiếp theo mô hình mạng chúng ta sẽ học một mô hình cao cấp hơn đó là mô hình thực thể mối kết hợp. Đây là một mô hình cao cấp hơn mô hình mạng và nó được sử dụng nhiều trong thiết kế dữ liệu ở mức quan niệm.', '<h2>1. Giới thiệu mô hình thực thể mối kết hợp</h2>\r\n\r\n<p><strong>Mô hình thực thể kết hợp</strong>&nbsp;(<em>Entity Relationshop Model</em>) viết tắt là (<em>ER</em>) &nbsp;được CHEN giới thiệu vòa năm 1976. Đây là một mô hình được sử dụng rộng rãi trong các bản thiết kế&nbsp;<a href=\"https://freetuts.net/tim-hieu-co-so-du-lieu-la-gi-va-he-quan-tri-csdl-mysql-168.html\" style=\"background: 0px 0px; transition: all 0.2s linear; color: rgb(240, 146, 23); outline: 0px; text-decoration: none;\" title=\"cơ sở dữ liệu\">cơ sở dữ liệu</a>&nbsp;ở mức quan niệm.</p>\r\n\r\n<p>So với mô hình mạng thì mô hình ER có nhiều ưu điểm hơn và nó thể hiện rõ hơn các thành phần trong thế giới thực. Ví dụ trong mô hình mạng ta chỉ biểu diễn các đối tượng chính chứ không mô tả được các đặc điểm trong đối tượng đó, vậy thì trong mô hình ER sẽ khắc phục được những điểm yếu này. Chính vì vậy việc lựa chọn mô hình này luôn là quyết định của các nhà phân tích thiết kế CSDL.</p>\r\n\r\n<h2>2. Loại thực thể trong mô hình ER</h2>\r\n\r\n<p><strong>Loại thực thể</strong>&nbsp;(<em>Entity Type</em>) là là những đối tượng hay sự vật của thế giới thực cần quản lý trong ứng dụng. Các đối tượng ở đây có thể sờ thấy và cũng có thể mang tính chất ảo tưởng.</p>\r\n\r\n<p><strong>Ví dụ</strong>: Khi thiết kế cơ sở dữ liệu quản lý học viên&nbsp;thì ta sẽ có một số thực thể như sau:</p>\r\n\r\n<ul>\r\n	<li>HOCVIEN</li>\r\n	<li>LOP</li>\r\n	<li>...</li>\r\n</ul>\r\n\r\n<p>Trong mô hình ER thì ta sẽ dùng ký hiệu sau để mô tả loại thực thể:</p>\r\n\r\n<ul>\r\n	<li><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/thuc-the.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></li>\r\n	<li><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/thuc-the-1.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:59px; margin-bottom:20px; max-width:100%; vertical-align:middle; width:199px\" /></li>\r\n	<li>...</li>\r\n</ul>\r\n\r\n<p>Như vậy ta dùng ký hiệu hình chữ nhật và bên trong chính là tên của loại thực thể.</p>\r\n\r\n<h2>3. Thực thể (Entity) trong mô hình ER</h2>\r\n\r\n<p>Trong&nbsp;<a href=\"https://freetuts.net/tag/lap-trinh-huong-doi-tuong\" style=\"background: 0px 0px; transition: all 0.2s linear; color: rgb(240, 146, 23); outline: 0px; text-decoration: none;\" title=\"lập trình hướng đối tượng\">lập trình hướng đối tượng</a>&nbsp;thì khi bạn tạo mới một lớp thì tức là bạn đã tạo một thể hiện của đối tượng mà class đó đang mô tả. Vậy thì trong mô hình thực thể mối kết hợp thì&nbsp;<strong>thực thể</strong>&nbsp;chính là một thể hiện của&nbsp;<strong>loại thực thể</strong>.</p>\r\n\r\n<p><strong>Ví dụ</strong>: &nbsp;Loại thực thể HOCVIEN gồm các thực thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Sinh viên Nguyễn Văn A</li>\r\n	<li>Sinh viên Nguyễn Văn B</li>\r\n	<li>Sinh viên Nguyễn Văn C</li>\r\n	<li>...</li>\r\n</ul>\r\n\r\n<p>Tất cả những&nbsp;<strong>học viên</strong>&nbsp;trên chính là thực thể thể hiện cho loại thực thể HOCVIEN.</p>\r\n\r\n<h2>4. Thuộc tính của loại thực thể (Entity Attribute)</h2>\r\n\r\n<p>Thuộc tính là những tính chất mô tả một đối tượng cụ thể nào đó. Ví dụ ta có chiếc mũ màu đỏ, chiếc áo màu vàng thì những màu sắc đỏ và vàng chính là những thuộc tính của mũ và áo.</p>\r\n\r\n<p>Nếu ở đây ta nhắm tới ví dụ ở phần trên thì với loại thực thể HOCVIEN ta sẽ có một số thuộc tính như sau:</p>\r\n\r\n<ul>\r\n	<li>Mahv</li>\r\n	<li>Hoten</li>\r\n	<li>Gioitinh</li>\r\n	<li>Ngaysinh</li>\r\n	<li>Noisinh</li>\r\n	<li>..</li>\r\n</ul>\r\n\r\n<p><strong>Và ta có ký hiệu như sau</strong>:</p>\r\n\r\n<p><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/thuoc-tinh-loai-thuc-the.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>\r\n\r\n<h2>5. Các loại thuộc tính của loại&nbsp;thực thể</h2>\r\n\r\n<p>Chúng ta có tổng cộng ba loại thuộc tính chính đó là&nbsp;<strong>Đơn vị</strong>,&nbsp;<strong>Đa hợp</strong>&nbsp;và&nbsp;<strong>Đa trị</strong>.</p>\r\n\r\n<ul>\r\n	<li><strong>Đơn vị</strong>&nbsp;(<em>simple</em>) là loại thuộc tính chỉ có một giá trị duy nhất. Ví dụ thuộc tính Noisinh, Mahv, ...</li>\r\n	<li><strong>Đa hợp</strong>&nbsp;(Composite) là thuộc tính có thể được tạo từ nhiều thành phần. Ví dụ thuộc tính Hoten có thể được tạo từ 3 thành phần đó là HỌ + TÊN LÓT + TÊN. Ký hiệu là HOTEN (HO, TENLOT, TEN)</li>\r\n	<li><strong>Đa trị</strong>&nbsp;(Multi valued) là loại thuộc tính có thể có nhiều giá trị đối với loại thực thể. Ví dụ bằng cấp thì ta có bằng loại tốt, loại khá, trung bình khá và giỏi. Ký hiệu là {BANGCAP}</li>\r\n</ul>\r\n\r\n<p>Với hai loại thuộc tính&nbsp;<strong>đa hơp</strong>&nbsp;và&nbsp;<strong>đa trị</strong>&nbsp; thì ta có thể thiết kế chồng lên nhau.</p>\r\n\r\n<p><strong>Ví dụ</strong>:&nbsp;{BANGCAP(TRUONGCAP, LOAIBANG, NGANH)}</p>\r\n\r\n<h2>6. Khóa của loại thực thể trong mô hình ER</h2>\r\n\r\n<p>Như định nghĩa trên thì mỗi&nbsp;<strong>thực thể</strong>&nbsp;sẽ là một thể hiện của&nbsp;<strong>loại thực thể</strong>, vậy thì làm sao để nhận diện các&nbsp;<strong>thực thể</strong>&nbsp;đó? Để nhận diện thì với mỗi&nbsp;<strong>thực thể</strong>&nbsp;chúng ta sẽ xác định cho nó một khóa duy nhất. Như vậy khóa của&nbsp;<strong>loại thực thể</strong>&nbsp;chính là dùng để nhận diện các&nbsp;<strong>thực thể</strong>&nbsp;của nó.</p>\r\n\r\n<p><strong>Ví dụ</strong>: trong&nbsp;<strong>loại thực thể</strong>&nbsp;HOCVIEN sẽ có nhiều&nbsp;<strong>thực thể</strong>&nbsp;học viên và mỗi học viên sẽ có một mã số duy nhất. Như vậy mã học viên chính&nbsp;là Key để ta nhận diện được học viên nào.</p>\r\n\r\n<h2>7. Các loại mối kết hợp trong mô hình ER</h2>\r\n\r\n<p>Trong mô hình mạng thì giữa hai đối tượng sẽ có một mối liên hệ và ta gọi nó là&nbsp;<strong>loại liên hệ</strong>. Vậy thì trong mô hình thực thể mối kết hợp ta gọi nó là loại mối kết hợp.&nbsp;Vậy loại mối kết hợp chính là mối liên hệ giữa hai loại thực thể.</p>\r\n\r\n<p><strong>Ví dụ</strong>: Giữa hai loại thực thể HOCVIEN và LOP ta sẽ có loại mối kết hợp THUOC, và ta biểu diễn như sau:</p>\r\n\r\n<p><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/loai-moi-ket-hop.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>\r\n\r\n<p>Biểu diễn bằng lời nói thì như sau \"<em>học viên sẽ thuộc lớp nào đó</em>\"</p>\r\n\r\n<p>Ngoài ra giữa hai loại thực thể có thể tồn tại nhiều loại mối kế hợp.</p>\r\n\r\n<p><strong>Ví dụ</strong>:Giữa hai thực thể HOCVIEN và LOP sẽ có hai loại mối kế hợp nhu sau:</p>\r\n\r\n<ul>\r\n	<li>Học viên sẽ thuộc lớp nào đó</li>\r\n	<li>Học viên sẽ là lớp trường lớp nào đó</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/loai-moi-ket-hop1.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>\r\n\r\n<h2>8. Số ngôi của loại mối kết hợp</h2>\r\n\r\n<p><strong>Số ngôi</strong>&nbsp;chính là số&nbsp;<strong>loại thực thể</strong>&nbsp;mà&nbsp;<strong>loại mối&nbsp;kết hợp</strong>&nbsp;đó gắn kết. Như trong ví dụ 2 ở phần 7 thì&nbsp;<strong>loại mối kết</strong>&nbsp;hợp THUOC sẽ có số ngôi là 2 tại vì nó gắn kết giữa&nbsp;hai&nbsp;<strong>loại thực thể</strong>&nbsp;HOCVIEN và LOP.</p>\r\n\r\n<p><strong>Ví dụ</strong>: Giả sử ta có ba loại thực thể là:</p>\r\n\r\n<ul>\r\n	<li>HOCVIEN : học viên</li>\r\n	<li>MONHOC : môn học</li>\r\n	<li>LANTHI : lần thi</li>\r\n</ul>\r\n\r\n<p>Ta sẽ có một&nbsp;<strong>loại mối kết hợp</strong>&nbsp;tên là THI để gắn ba&nbsp;<strong>loại thực thể</strong>&nbsp;này theo mô hình như sau:</p>\r\n\r\n<p><img alt=\"so-ngoi-trong-mo-hinh-er.png\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/so-ngoi-trong-mo-hinh-er.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>\r\n\r\n<p>Như vậy&nbsp;<strong>loại mối kết</strong>&nbsp;hợp THI sẽ có số ngôi là 3</p>\r\n\r\n<h2>9. Bản số</h2>\r\n\r\n<p>Trong mô hình mạng thì ta cũng có sử dụng bản số để chỉ ra số lượng các mẫu tin tham gia vào các mối quan hệ, vậy thì trong mô hình ER cũng không có gì khắc biệt, chúng ta sử dụng những bản số để thể hiện số lượng tối thiểu và số lượng tối đa tham gia vào loại mối kết hợp.</p>\r\n\r\n<p><strong>Ký hiệu</strong>:&nbsp;<code>(min, max)</code></p>\r\n\r\n<p><strong>Ví dụ</strong>: &nbsp;Loại mối kết hợp học viên (HOCVIEN) và lớp (LOP) có loại mối kết hợp thuộc:</p>\r\n\r\n<p><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/ban-so.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>\r\n\r\n<p><strong>Có nghĩa như sau</strong>:</p>\r\n\r\n<ul>\r\n	<li>Mỗi học viên sẽ thuộc một lớp</li>\r\n	<li>Mỗi lớp có thể có 1 hoặc nhiều học viên</li>\r\n</ul>\r\n\r\n<p>Chi tiết thì bạn xem lại bài&nbsp;<a href=\"https://freetuts.net/mo-hinh-du-lieu-mang-173.html\" style=\"background: 0px 0px; transition: all 0.2s linear; color: rgb(240, 146, 23); outline: 0px; text-decoration: none;\" title=\"mô hình dữ liệu mạng\">mô hình dữ liệu mạng</a>&nbsp;nhé.</p>\r\n\r\n<h2>10. Mô hình thực thể mối kết hợp mở rộng</h2>\r\n\r\n<h3>Chuyên biệt hóa</h3>\r\n\r\n<p>Giả sử ta có hai&nbsp;<strong>loại thực tể</strong>&nbsp;là GIAOVIEN và HOCVIEN thì nếu để ý kỹ thì bạn sẽ nhận ra rằng cả hai đều là&nbsp;<strong>loại thực thể</strong>&nbsp;CONNGUOI. Tại sao ta lại phân ra làm hai loại như vậy mà không gộp chung lại một? Lý do là mỗi loại thực thể sẽ có một số thuộc tính khác nhau. Và nếu để ý kỹ hơn nữa thì ta thấy loại thực thể CONNGUOI sẽ chứa một số thuộc tính dùng chung cho cả hai loại thực thể GIAOVIEN và HOCVIEN.</p>\r\n\r\n<ul>\r\n	<li>Với GIAOVIEN ta sẽ có một số thuộc tính như (MaGiaoVien,&nbsp;CapBac, ChuNhiemLop, ...)</li>\r\n	<li>Với HOCVIEN ta sẽ có một số thuộc tính như (MaHocVien,&nbsp;Lop, Khoa, ...)</li>\r\n	<li>Với CONNGUOI ta sẽ có một số thuộc tính như (SOCMND, GioiTinh, ...)</li>\r\n</ul>\r\n\r\n<p>Ta có một sơ đồ ER mở rộng như sau:</p>\r\n\r\n<p><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/mo-hinh-er-mo-rong.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>\r\n\r\n<h3>Mối kết hợp đệ quy</h3>\r\n\r\n<p>Mối kết hợp đệ quy là loại mối kết hợp được tạo thành từ cùng một loại thực thể, hay nói cách dễ hiểu thì loại thực thể đó tự trỏ lại chính nó.</p>\r\n\r\n<p>Ví dụ trong công ty ABC có danh sách nhân viên và trong đó có một nhân viên làm trưởng nhóm (leader) quản lý tất cả các nhân viên còn lại. Mô hình như sau:</p>\r\n\r\n<p><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/moi-ket-hop-de-quy.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>\r\n\r\n<h3>Loại thực thể yếu</h3>\r\n\r\n<p>Trong một số chương trình quản lý phần mềm ta phân tích dữ liệu sẽ có những loại thực thể quan trọng và một số loại thực thể không quan trọng và ta gọi nó là loại thực thể yếu. Loại thực thể yếu sẽ có một số tính chất như sau:</p>\r\n\r\n<ul>\r\n	<li>Không có thuộc tính khóa</li>\r\n	<li>Bắt buộc phải tham gia vào loại mối kết hợp với chủ thể của nó</li>\r\n</ul>\r\n\r\n<p>Ký hiệu của nó như sau: Chú ý đường viền hơi đậm so với loại thực thể bình thường.</p>\r\n\r\n<p><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/loai-thuc-the-yeu.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>\r\n\r\n<p><strong>Ví dụ</strong>: Loại thực thể Thân Nhân (THANNHAN) là loại thực thể yếu gồm các thuộc tính (Số thứ tự, tên, ngày sinh, Mối quan hệ) và tham gia vào loại mối kết hợp CÓ với loại thực thể NHANVIEN.</p>\r\n\r\n<p><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/loai-thuc-the-yeu-1.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>\r\n\r\n<h3>Mối kết hợp mở rộng</h3>\r\n\r\n<p>Các loại thực thể có thể có nhiều loại mối kết hợp với nhau. Ví dụ ta có loại thực thể LOP, MONHOC, GIAOVIEN sẽ có các loại mối kết hợp như sau:</p>\r\n\r\n<ul>\r\n	<li>LOP sẽ học MONHOC</li>\r\n	<li>GIAOVIEN sẽ giảng dạy LOP</li>\r\n	<li>GIAOVIEN sẽ giảng dạy MONHOC</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"https://freetuts.net/upload/tut_post/images/2015/02/11/305/loai-moi-ket-hop-mo-rong.png\" style=\"border-color:rgb(229, 229, 229) !important; border-image:initial; border-style:solid; border-width:1px; height:auto; margin-bottom:20px; max-width:100%; vertical-align:middle\" /></p>', '1.png', '2017-04-03 01:36:34', '2017-04-03 01:36:34', 6, 12),
